commands.input_pattern:
SELECT n\.nspname as "[^"]*",[ 
]+c\.relname as "[^"]*",[ 
]+CASE c\.relkind (WHEN '.' THEN '[^']*' )+END as "[^"]*",[ 
]+pg_catalog\.pg_get_userbyid\(c\.relowner\) as "[^"]*"
FROM pg_catalog\.pg_class c[ 
]+LEFT JOIN pg_catalog\.pg_namespace n ON n\.oid = c\.relnamespace(?:[ 
]+LEFT JOIN pg_catalog\.pg_am am ON am\.oid = c\.relam)?
WHERE c\.relkind IN \('r'(?:,'p')?,'v','m','S','f',''\)[ 
]+AND n\.nspname <> 'pg_catalog'[ 
]+AND (?:n\.nspname <> 'information_schema'[ 
]+AND )?n\.nspname !~ '\^pg_toast'[ 
]+AND (?:n\.nspname <> 'information_schema'[ 
]+AND )?pg_catalog\.pg_table_is_visible\(c\.oid\)
ORDER BY 1,2;?$

^SELECT c\.oid,[ 
]+n\.nspname,[ 
]+c\.relname
FROM pg_catalog\.pg_class c[ 
]+LEFT JOIN pg_catalog\.pg_namespace n ON n\.oid = c\.relnamespace
WHERE c\.relname OPERATOR\(pg_catalog\.~\) '\^\((?<tablename>.*)\)\$'(:? COLLATE pg_catalog.default)?[ 
]+AND pg_catalog\.pg_table_is_visible\(c\.oid\)
ORDER BY 2, 3;?$

^SELECT a\.attname,[ 
]+pg_catalog\.format_type\(a\.atttypid, a\.atttypmod\),[ 
]+\(SELECT (substring\()?pg_catalog\.pg_get_expr\(d\.adbin, d\.adrelid(?:, true)?\)( for 128\))?[ 
]+FROM pg_catalog\.pg_attrdef d[ 
]+WHERE d\.adrelid = a\.attrelid AND d\.adnum = a\.attnum AND a\.atthasdef\),[ 
]+a\.attnotnull,(?: a\.attnum,)?[ 
]+NULL AS attcollation,[ 
]+(?:''::pg_catalog\.char AS attidentity,[ 
]+''::pg_catalog\.char AS attgenerated
|  NULL AS indexdef,[ 
]+NULL AS attfdwoptions
)?([ ]*''::pg_catalog.char AS attidentity)?[ 
]*FROM pg_catalog\.pg_attribute a
WHERE a\.attrelid = '(?<tablename>.*)' AND a\.attnum > 0 AND NOT a\.attisdropped
ORDER BY a\.attnum;?$

^SELECT c\.oid::pg_catalog\.regclass[ 
]+FROM pg_catalog\.pg_class c, pg_catalog\.pg_inherits i[ 
]+WHERE c\.oid( )?=( )?i\.inhparent AND i\.inhrelid = '.*'[ 
]+(?:AND c\.relkind != 'p')?[ 
]+(?:AND c\.relkind != 'I')?[ 
]*ORDER BY inhseqno;?$

^SELECT c\.oid::pg_catalog\.regclass(, c\.relkind(, false AS inhdetachpending)?, NULL)?[ 
]+FROM pg_catalog\.pg_class c, pg_catalog\.pg_inherits i[ 
]+WHERE c\.oid( )?=( )?i\.inhrelid AND i\.inhparent = '.*'[ 
]+ORDER BY c\.relname;?$

^SELECT n\.nspname as "[^"]*",[ 
]+c\.relname as "[^"]*",[ 
]+CASE c\.relkind (WHEN '.' THEN '[^']*' )+END as "[^"]*",[ 
]+pg_catalog\.pg_get_userbyid\(c\.relowner\) as "[^"]*"
FROM pg_catalog\.pg_class c[ 
]+LEFT JOIN pg_catalog\.pg_namespace n ON n\.oid = c\.relnamespace
WHERE c\.relkind IN \('r'(?:,'p')?,''\)[ 
]+AND n\.nspname <> 'pg_catalog'[ 
]+AND (?:n\.nspname <> 'information_schema'[ 
]+AND )?n\.nspname !~ '\^pg_toast'[ 
]+AND (?:n\.nspname <> 'information_schema'[ 
]+AND )?pg_catalog\.pg_table_is_visible\(c\.oid\)
ORDER BY 1,2;?$

