^SELECT n\.nspname as "[^"]*",[ 
]+c\.relname as "[^"]*",[ 
]+CASE c\.relkind (WHEN '.' THEN '[^']*' )+END as "[^"]*",[ 
]+pg_catalog\.pg_get_userbyid\(c\.relowner\) as "[^"]*",[ 
]+c2\.relname as "[^"]*"
FROM pg_catalog\.pg_class c[ 
]+LEFT JOIN pg_catalog\.pg_namespace n ON n\.oid = c\.relnamespace[ 
]+LEFT JOIN pg_catalog\.pg_index i ON i\.indexrelid = c\.oid[ 
]+LEFT JOIN pg_catalog\.pg_class c2 ON i\.indrelid = c2\.oid
WHERE c\.relkind IN \('i'(?:,'I')?,''\)[ 
]+AND n\.nspname <> 'pg_catalog'[ 
]+AND (?:n\.nspname <> 'information_schema'[ 
]+AND )?n\.nspname !~ '\^pg_toast'[ 
]+AND (?:n\.nspname <> 'information_schema'[ 
]+AND )?pg_catalog\.pg_table_is_visible\(c\.oid\)
ORDER BY 1,2;?$

^SELECT n\.nspname as "[^"]*",[ 
]+c\.relname as "[^"]*",[ 
]+CASE c\.relkind (WHEN '.' THEN '[^']*' )+END as "[^"]*",[ 
]+pg_catalog\.pg_get_userbyid\(c\.relowner\) as "[^"]*",[ 
]+c2\.relname as "[^"]*"
FROM pg_catalog\.pg_class c[ 
]+LEFT JOIN pg_catalog\.pg_namespace n ON n\.oid = c\.relnamespace[ 
]+LEFT JOIN pg_catalog\.pg_index i ON i\.indexrelid = c\.oid[ 
]+LEFT JOIN pg_catalog\.pg_class c2 ON i\.indrelid = c2\.oid
WHERE c\.relkind IN \('i'(?:,'I')?,'s',''\)[ 
]+AND (?:n\.nspname !~ '\^pg_toast'[ 
]+AND )?c\.relname OPERATOR\(pg_catalog\.~\) '\^\((?<indexname>.*)\)\$'[ 
]+AND pg_catalog\.pg_table_is_visible\(c\.oid\)
ORDER BY 1,2;?$

^SELECT pg_catalog\.quote_ident\(attname\)[ 
]+FROM pg_catalog\.pg_attribute a, pg_catalog\.pg_class c[ 
]+WHERE c\.oid = a\.attrelid[ 
]+AND a\.attnum > \d+[ 
]+AND NOT a\.attisdropped[ 
]+AND substring\(pg_catalog\.quote_ident\(attname\),\d+,\d+\)='(?<attrprefix>.*)'[ 
]+AND \(pg_catalog\.quote_ident\(relname\)='(?<tablename>.*)'[ 
]+OR '"' \|\| relname \|\| '"'='.*'\)[ 
]+AND pg_catalog\.pg_table_is_visible\(c\.oid\)
LIMIT 1000$

SELECT pg_catalog\.quote_ident\(nspname\) FROM pg_catalog\.pg_namespace[ 
]+WHERE substring\(pg_catalog\.quote_ident\(nspname\),\d+,\d+\)='(?<prefix>.*)'
LIMIT \d+
